import React, { createContext, useContext, useState } from 'react';
import {jwtDecode} from "jwt-decode";

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(() => {
        const authTokenCookie = document.cookie.split('; ').find(cookie => cookie.startsWith('auth_token='));
        if (authTokenCookie) {
            return authTokenCookie.split('=')[1];
        }
        return null;
    });
    const [isAdmin, setIsAdmin] = useState(false);

    const login = (newToken) => {
        document.cookie = `auth_token=${newToken}; Path=/; Secure; SameSite=Strict`;
        setToken(newToken);
        const decodedToken = jwtDecode(newToken);
        setIsAdmin(decodedToken.role === 'ADMIN');
    };

    const logout = () => {
        document.cookie = 'auth_token=; Path=/; Expires=Thu, 01 Jan 1970 00:00:00 GMT; Secure; SameSite=Strict'; 
        setToken(null);
        setIsAdmin(false);
    };

    const isAuthenticated = () => {
        // Check if token exists
        if (!token) {
            return false;
        }

        // Check if token is expired
        const decodedToken = jwtDecode(token);
        const currentTime = Date.now() / 1000;
        if (decodedToken.exp < currentTime) {
            // Token is expired
            logout();
            return false;
        }

        return true;
    };

    const value = {
        token,
        login,
        logout,
        isAuthenticated,
        isAdmin
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
