import './RegisterPage.css';
import { useState } from "react";
import RegisterButton from "../components/RegisterButton";

const RegisterPage = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [errors, setErrors] = useState({});

    const validateField = (fieldName, value) => {
        const namePattern = /^[A-Za-z]+$/;
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        switch (fieldName) {
            case 'firstName':
                return namePattern.test(value.trim()) ? '' : `Please enter a valid first name`;
            case 'lastName':
                return namePattern.test(value.trim()) ? '' : `Please enter a valid last name`;
            case 'email':
                return emailPattern.test(value) ? '' : 'Please enter a valid email address';
            case 'password':
                return value.length >= 6 ? '' : 'Password must be at least 6 characters long';
            case 'confirmPassword':
                return value === password ? '' : 'Passwords do not match';
            default:
                return '';
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setErrors({});

        const fieldsToValidate = ['firstName', 'lastName', 'email', 'password', 'confirmPassword'];
        const newErrors = {};
        let isValid = true;

        fieldsToValidate.forEach(fieldName => {
            const fieldValue = formState[fieldName]; // Access state variable directly
            const fieldError = validateField(fieldName, fieldValue);
            if (fieldError) {
                newErrors[fieldName] = fieldError;
                isValid = false;
            }
        });

        if (isValid) {
            console.log('Registration data:', { firstName, lastName, email, password });
        } else {
            setErrors(newErrors);
        }
    };

    return (
        <div className="register-page-container">
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                />
                {errors.firstName && <div className="error-message">{errors.firstName}</div>}
                <input
                    type="text"
                    placeholder="Last Name"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
                {errors.lastName && <div className="error-message">{errors.lastName}</div>}
                <input
                    type="text"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                {errors.email && <div className="error-message">{errors.email}</div>}
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {errors.password && <div className="error-message">{errors.password}</div>}
                <input
                    type="password"
                    placeholder="Confirm Password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                />
                {errors.confirmPassword && <div className="error-message">{errors.confirmPassword}</div>}
                <RegisterButton />
            </form>
        </div>
    );
};

export default RegisterPage;
