import React, {createContext, useContext, useEffect, useState} from 'react';
import {jwtDecode} from "jwt-decode";
import Cookies from "js-cookie";

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    const [token, setToken] = useState(() => {
        return Cookies.get('auth_token') || "GAY";
    });
    const [isAdmin, setIsAdmin] = useState(false);

    useEffect(() => {
        // Decode the token to check if the user is an admin
        if (token !== null) {
            const decodedToken = jwtDecode(token);
            setIsAdmin(decodedToken.role === 'ADMIN');
        }
    }, [token]);
    const login = (newToken) => {
        document.cookie = `auth_token=${newToken}; Path=/; Secure; SameSite=Strict`;
        setToken(newToken);
        const decodedToken = jwtDecode(newToken);
        setIsAdmin(decodedToken.role === 'ADMIN');
    };

    const logout = () => {
        document.cookie = 'auth_token=; Path=/; Expires=Thu, 01 Jan 1970 00:00:00 GMT; Secure; SameSite=Strict';
        setToken("gay");
        setIsAdmin(false);
    };

    const isAuthenticated = () => {
        if (!token) {
            return false;
        }

        const decodedToken = jwtDecode(token);
        const currentTime = Date.now() / 1000;
        if (decodedToken.exp < currentTime) {
            // Token is expired
            logout();
            return false;
        }

        return true;
    };

    const value = {
        token,
        login,
        logout,
        isAuthenticated,
        isAdmin
    };

    return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};
