import './RegisterPage.css'
import {useState} from "react";
import RegisterButton from "../components/RegisterButton";
const RegisterPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [errors, setErrors] = useState({});

    const validateFirstName = () => {
        // Name validation logic
        const namePattern = /^[A-Za-z]+$/; // Pattern to allow only alphabetic characters
        if (!namePattern.test(firstName)){
            setErrors(prevErrors => ({ ...prevErrors, firstName: 'Please enter a valid first name'}));
            return false;
        }
        return true;
    };
    const validateEmail = () => {
        // Email validation logic (regex or other validation methods)
        // For example, regex pattern for email validation
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailPattern.test(email)) {
            setErrors(prevErrors => ({ ...prevErrors, email: 'Please enter a valid email address' }));
            return false;
        }
        return true;
    };

    const validatePassword = () => {
        // Password validation logic
        if (password.length < 6) {
            setErrors(prevErrors => ({ ...prevErrors, password: 'Password must be at least 6 characters long' }));
            return false;
        }
        return true;
    };

    const validateConfirmPassword = () => {
        // Confirm password validation logic
        if (password !== confirmPassword) {
            setErrors(prevErrors => ({ ...prevErrors, confirmPassword: 'Passwords do not match' }));
            return false;
        }
        return true;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Reset errors
        setErrors({});

        // Perform field validations
        const isValidEmail = validateEmail();
        const isValidPassword = validatePassword();
        const isValidConfirmPassword = validateConfirmPassword();
        const isValidFirstName = validateFirstName();

        // If all validations pass, proceed with registration
        if (isValidEmail && isValidPassword && isValidConfirmPassword && isValidFirstName) {
            // Here you can add your registration logic
            // For simplicity, just log the registration data
            console.log('Registration data:', { firstName, lastName, email, password });
        }
    };

    return (
        <div className="register-page-container">
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="First Name"
                    value={firstName}
                    onChange={(e) => setFirstName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Last Name"
                    value={lastName}
                    onChange={(e) => setLastName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                {errors.email && <div className="error-message">{errors.email}</div>}
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {errors.password && <div className="error-message">{errors.password}</div>}
                <input
                    type="password"
                    placeholder="Confirm Password"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                />
                {errors.confirmPassword && <div className="error-message">{errors.confirmPassword}</div>}
                <RegisterButton />
            </form>
        </div>
    );
};

export default RegisterPage
